import './About.css';
import emoji1 from './Assets/Upside-Down Face.svg';
import emoji2 from './Assets/Slightly Smiling Face.svg';
import emoji3 from './Assets/Grinning Face.svg';
import emoji4 from './Assets/Grinning Face with Sweat.svg';
import about1 from './Assets/abt-img1.png';
import about2 from './Assets/abt-img2.png';
import groupImage from './Assets/img-grp.png';
import thunder from './Assets/thunder-bolt.svg';
import PurpleBtn from '../Button/PurpleButton';

function About(){
    return (
        <div className="About">
            <div className="about1">
                <img src={about1} />
                <div className="about1-bottom">
                    <p>Best rating</p>
                    <div className="emoji-section">
                        <span className="emoji">
                            <img src={emoji1} />
                        </span>
                        <span className="emoji emoji2">
                            <img src={emoji2} />                            
                        </span>
                        <span className="emoji emoji2">
                            <img src={emoji3} />                            
                        </span>
                        <span className="emoji emoji4">
                            <img src={emoji4} />                            
                        </span>
                    </div>
                </div>
            </div>
            <div className="about2">
                <div className="about2-top">
                <p><span>6M+</span> <span><img src={thunder} /></span></p>
                <p>Product sales per year using <br />our too</p>
                <img src={groupImage} />
                </div>
            </div>
            <div className="about3">
                <h2>About</h2>
                <p>When your skin’s oil glands go into overdrive, they leave behind a slick, waxy <br />substance called sebum. A serum may be able to help give your skin care <br />routine a boost, controlling oil and offering a few added benefits along the way. <br />We’ve put together this list of the best skin serums for oily skin to give you a <br />glow you can feel good about.</p>
            <PurpleBtn />
            </div>
        </div>
    );
}

export default About;
